import React from "react";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Navbar.css";
function Navbar() {
  const username = localStorage.getItem('username');
  const [search, setSearch] = useState('');
  const navigate = useNavigate();
  const handleSubmit = async (e) => {
    console.log(search)
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:8000/search/", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ search_query: search}),
      });
      if (response.ok) {
        const search_result = await response.json();
        console.log("Search submitted successfully",search_result[0].name);
        navigate(`/Movie_Page/${search_result[0].name}`);
      } else {
        console.log("Error");
      }
    } catch (error) {
      console.error("Error: ",error);
    }
  }
  const handleChange=(e)=> {
    setSearch(e.target.value);
  }
  return (
    <nav className="navbar navbar-expand-sl navbar-dark bg-dark">
      <div className="container-fluid">
        {/* Image tag on the leftmost side with reference to the home page */}
        <a className="navbar-brand" href="/home">
          <img src="logo.png" id="Logo" alt="Movie Mania" width={60} />
        </a>
        {/* Search bar in the middle */}
        <form className="form-inline my-2 my-lg-0 mx-auto" onSubmit={handleSubmit}>
          <input
            className="form-control mr-sm-2"
            type="search"
            placeholder="Search movies..."
            aria-label="Search"
            value={search}
            onChange={handleChange}
          />
          <button
            className="btn btn-outline-warning my-2 my-sm-0"
            type="submit"
          >
            Search
          </button>
        </form>
        {/* SignIn button on the right */}
        <a href="/login">
          <button className="btn btn-outline-warning ml-auto" type="button">{(username == null)?"Sign Up / LogIn":username}</button>
        </a>
      </div>
    </nav>
  );
}
export default Navbar;
