// Code Written By: Tejas Chaudhari
import React, { useEffect } from "react";
import Navbar from "../components/Navbar";
import Welcome from "../components/Welcome";
import Recommendations from "../components/Recommendations";
import YourRatings from "../components/YourRatings";
import Footer from "../components/Footer";
import { useState } from "react";
function Home() {
  const [username, setUsername] = useState('');
  const [top6, setTop6] = useState([{'name': '','poster':''},{'name': '','poster':''},{'name': '','poster':''},{'name': '','poster':''},{'name': '','poster':''},{'name': '','poster':''}]);
  useEffect(() => {
    if(localStorage.getItem('username')==''){
      const fetchData = async () => {
        try {
          const response = await fetch("http://localhost:8000/home/");
          if (!response.ok) {
            throw new Error(`HTTP error ${response.status}`);
          }
          const data = await response.json();
          setTop6(data);
        } catch (error) {
          console.error("Error fetching data:", error);
          // Display a user-friendly error message
          alert("An error occurred while fetching the data. Please try again later.");
        }};
    } else{
      const fetchData = async () => {
        try{
          const response = await fetch("http://localhost:8000/home/", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ username: localStorage.getItem('username')}),
          });
          if (!response.ok) {
            throw new Error(`HTTP error ${response.status}`);
          }
          const data = await response.json();
          setTop6(data);
        } catch (error) {
          console.error("Error fetching data:", error);
          // Display a user-friendly error message
          alert("An error occurred while fetching the data. Please try again later.");
        }
      };
      fetchData();
    }
  
  }, []);
  return <>
    <Navbar username={username}/>
    <Welcome />
    <Recommendations top6={top6}/>
    
    <div><br></br></div>   
    <Footer />
  </>;
}

export default Home;
